var _=require('underscore');

module.exports = function(config){
    var type='rtcp';
    if(_.has(config,'type')){
        type=config.type;
    }
    var path='./lib/'+type+'.js';
    try{
        var mod= require(path);
        var cli=new mod();
        cli.init(config);
        return cli;
    }
    catch (e) {
        return e;
    }
};
